mapper.select:
SELECT COUNT(*)
  FROM ATT_FILE A
 WHERE A.REF_CODE = #{ member_code }

mapper.insert:
INSERT
  INTO ATT_FILE A
    (
      A.ATTACHMENT_NO
    , A.REF_BOARD_NO
    , A.ORIGINAL_NAME
    , A.SAVED_NAME
    , A.SAVE_PATH
    , A.FILE_TYPE
    , A.THUMBNAIL_PATH
    , A.ATTACHED_STATUS
    )
VALUES
    (
      'ATT_'||ATT_NO_SEQ.NEXTVAL
    , #{ refCode }
    , #{ originName }
    , #{ saveName }
    , #{ savePath }
    , #{ fileType }
    , #{ thumbnailPath }
    , 'Y'
    )
